var pkey			= 42699;
var can_make_1		= true;
var max_slide_1		= 20;
var price_slide_1	= 1;
var max_count_1		= 74;
var start_count_1	= 1;
var step_count_1	= 4;
var time_slide_1	= 15;

var can_make_2		= true;
var max_slide_2		= 20;
var price_slide_2	= 1;
var max_count_2		= 74;
var start_count_2	= 1;
var step_count_2	= 4;
var time_slide_2	= 10;

/**
*
*	JS TabControl
*
*/
function localHouseStabsHandler() {
	var t   = {};
	var curRel = 1;
	var newRel = curRel;
	t.startSelected  = [curRel];

	/** запускаем обработчики и т.п. */
	t.start     = function() {
		for(var n in this.startSelected) {
			var rel = this.startSelected[n];
			$('.tab[rel='+rel+']').removeClass('hidden');
			$('.tabs_buttons > .btn[rel='+rel+']').addClass('selected');
		}
	}

	/** при клике по табу */
	t.click      = function(newRel) {               
		$('.tab[rel='+curRel+']').addClass('hidden');
		$('.tabs_buttons > .btn[rel='+curRel+']').removeClass('selected');

		$('.tab[rel='+newRel+']').removeClass('hidden');   
		$('.tabs_buttons > .btn[rel='+newRel+']').addClass('selected');                                      
		curRel = newRel;
	}

	return t;
}

