function AlchemySetType(potion_type) {
	$("td[id^='alch_select_type_']").removeClass('shown_type');
	$('#alch_select_type_' + potion_type).addClass('shown_type');

	selected_potion_type	= potion_type;

	$("div[id^='potion_choose_']").removeClass('hidden');
	$("div[id^='potion_choose_']").addClass('hidden');
	$("#potion_choose_" + potion_type).removeClass('hidden');

	checkParamsDisp();
}

function AlchemySetPotion(potion_id) {
	if ($('#alchemy_potion_' + selected_potion_type + '_' + potion_id + ' img').hasClass('alchemy_selected')) {
		$('#alchemy_potion_' + selected_potion_type + '_' + potion_id + ' img').removeClass('alchemy_selected');
		$('#alchemy_potion_' + selected_potion_type + '_' + potion_id + ' img').addClass('alchemy_img_pos');
	}else {
		$('#alchemy_potion_' + selected_potion_type + '_' + potion_id + ' img').addClass('alchemy_selected');
		$('#alchemy_potion_' + selected_potion_type + '_' + potion_id + ' img').removeClass('alchemy_img_pos');
	}

	checkParamsDisp();
}

function checkParamsDisp() {
	var summ=0;
	selected_array	= [];

	$("div[id^='alchemy_potion_" + selected_potion_type + "_']").each(function(i) {
		if ($('#' + $(this).attr('id') + ' img').hasClass('alchemy_selected')) {
			summ += parseInt($('#' + $(this).attr('id') + ' img').attr('count'));
			selected_array.push($(this).attr('sub_id'));
		}
	});

	var full_q	= Math.floor(summ/100);
	var rest_q	= summ%100;

	if (full_q <= 0) {
		$('#alch_disp_rez_1').removeClass('hidden');
		$('#alch_disp_rez_2').addClass('hidden');
		$('#disp_val').html(rest_q);
		$('#disp_form_inp_1').val(JSON.stringify(selected_array));
	}else {
		$('#alch_disp_rez_2').removeClass('hidden');
		$('#alch_disp_rez_1').addClass('hidden');
		$('#disp_full_count').html(full_q);
		$('#disp_val_other').html(rest_q);
		if (rest_q <= 0) $('#need_or_not_to_show').hide();
		$('#disp_form_inp_2').val(JSON.stringify(selected_array));
	}
}

function startMake(type) {
	if (type == 1) {
		var add_get		= '&do_cmd=make_alchemy_1&k=' + pkey + '&make_count_1=' + $("#slider_v_1").val();

		$.getJSON('?a=myguild&id=31&m=lab' + add_get, function(data){
			if (data.status == 'ok') {
				pkey			= data.data.GET_KEY;
				can_make_1		= data.data.can_make_1;
				max_slide_1		= data.data.max_slide_1;
				price_slide_1	= data.data.price_slide_1;
				max_count_1		= data.data.max_count_1;
				start_count_1	= data.data.start_count_1;
				step_count_1	= data.data.step_count_1;
				time_slide_1	= data.data.time_slide_1;

				if (data.data.started_1 == 1) {
					$('#alchemy_1_none').addClass('hidden');
					$('#alchemy_1_work').removeClass('hidden');
					$('#alchemy_1_work_text').html(data.data.start_text_1);
					$('#alchemy_1_work_timer').html(data.data.start_timer_1);
				}else {
					$('#alchemy_1_none').removeClass('hidden');
					$('#alchemy_1_work').addClass('hidden');
					$('#workshop_message_1').html(data.data.message_1);
				}

				SetMin(1);
			}else {
				$('#workshop_message_1').html(data.data);
			}
			if (data.update != null)
				doUpdateInfo(data.update);
		});
	}else if (type == 2) {
		var add_get		= '&do_cmd=make_alchemy_2&k=' + pkey + '&make_count_2=' + $("#slider_v_2").val();

		$.getJSON('?a=myguild&id=31&m=lab' + add_get, function(data){
			if (data.status == 'ok') {
				pkey			= data.data.GET_KEY;
				can_make_2		= data.data.can_make_2;
				max_slide_2		= data.data.max_slide_2;
				price_slide_2	= data.data.price_slide_2;
				max_count_2		= data.data.max_count_2;
				start_count_2	= data.data.start_count_2;
				step_count_2	= data.data.step_count_2;
				time_slide_2	= data.data.time_slide_2;

				if (data.data.started_2 == 1) {
					$('#alchemy_2_none').addClass('hidden');
					$('#alchemy_2_work').removeClass('hidden');
					$('#alchemy_2_work_text').html(data.data.start_text_2);
					$('#alchemy_2_work_timer').html(data.data.start_timer_2);
				}else {
					$('#alchemy_2_none').removeClass('hidden');
					$('#alchemy_2_work').addClass('hidden');
					$('#workshop_message_2').html(data.data.message_2);
				}

				SetMin(1);
			}else {
				$('#workshop_message_2').html(data.data);
			}
			if (data.update != null)
				doUpdateInfo(data.update);
		});
	}
}

function SetMin(id) {
	if ((id == 1 && can_make_1) || (id == 2 && can_make_2)) {
		$("#slider_" + id).slider("value", 1);
		$("#slider_v_" + id).val(1);
		UpdateHm(id);
	}
}

function SetMax(id) {
	if (id == 1 && can_make_1) {
		$("#slider_" + id).slider("value", max_slide_1);
		$("#slider_v_" + id).val(max_slide_1);
		UpdateHm(id);
	}else if (id == 2 && can_make_2) {
		$("#slider_" + id).slider("value", max_slide_2);
		$("#slider_v_" + id).val(max_slide_2);
		UpdateHm(id);
	}
}

function UpdateHm(id) {
	if (id == 1 && can_make_1) {
		var value	= $("#slider_v_" + id).val();
		var count	= parseInt(start_count_1) + (value - 1) * step_count_1;
		var price	= (value - 1) * price_slide_1;
		var time	= value * time_slide_1;

		count	= count > max_count_1 ? max_count_1 : count;
		$("#workshop_count_1").html(count);
		$("#workshop_price_1").html(price);
		$("#workshop_need_11").html(value);
		$("#workshop_need_12").html(value);
		$("#workshop_time_1").html(time);
		if (price == 0)
			$("#workshop_show_1").addClass('hidden');
		else
			$("#workshop_show_1").removeClass('hidden');

	}else if (id == 2 && can_make_2) {
		var value	= $("#slider_v_" + id).val();
		var count	= parseInt(start_count_2) + (value - 1) * step_count_2;
		var price	= (value - 1) * price_slide_2;
		var time	= value * time_slide_2;

		count	= count > max_count_2 ? max_count_2 : count;
		$("#workshop_count_2").html(count);
		$("#workshop_price_2").html(price);
		$("#workshop_need_2").html(value);
		$("#workshop_time_2").html(time);
		if (price == 0)
			$("#workshop_show_2").addClass('hidden');
		else
			$("#workshop_show_2").removeClass('hidden');
	}
}

