var onscroll_interval	= 0;
window.onscroll		= function(e) {
	var vScroll = document.documentElement.scrollTop || document.body.scrollTop;
	if (vScroll > 200) 
		$('#path_go_to_top').removeClass('hidden');
	else
		$('#path_go_to_top').addClass('hidden');

	clearTimeout(onscroll_interval);

	if (vScroll > 900) 
		onscroll_interval	= setTimeout(function(){ 
		globalPrepareGoToTop(vScroll)
	},100);
}

function globalPrepareGoToTop(vScroll) {
	var vScroll	= vScroll || 0;
	var free_width	= $('#content-container').offset().left;
	var w		= Math.max(free_width + 40,40);

	if (vScroll > $('#menu').offset().top+200)
		w	+= 200;

	if (free_width < 105) {
		/** маленькое разрешение */
		$('#path_go_to_top .go_to_top_bgr').addClass('go_to_top_mini');
		w	= Math.max(free_width - 30,40);
	} 

	$('#path_go_to_top').width(w).unbind().click(function(){
		globalPrepareGoToTop(0);
		 $('body,html').animate({
			scrollTop: 0
		}, 400);
	});
}
