function setReadWiki() {
	$.getJSON('/news.php?type=bwiki&json=2', function(data){
		if (data.status == 'ok') {
			return true;
		}else {
			return false;
		}
	});
	return false;
}

div.script:
var pkey	= 23336;

$(document).ready(function(){
	blockForms();
});

function AlchemySetPotion(potion_type) {
	$('img').filter(".alchemy_selected").attr('class', 'alchemy_img_pos');
	$('#alchemy_potion_' + potion_type + ' img').attr('class', 'alchemy_selected');
	getPotion(potion_type);
}

function getPotion(potion_type) {
	var add_get	= '&potion_type=' + potion_type + '&key=' + pkey;

	$.getJSON('?a=myguild&id=31&m=potion_1' + add_get, function(data){
		if (data.status == 'ok') {
			pkey	= data.data.GET_KEY;
			$('#right_main').html(data.data.action_window);
			$('#alchemy_potion_name_need').html(data.data.selected.potion.item_info.name);
			addItemInfo();

			return true;
		}else {
			$('#alchemy_error_msg').html(data.data);
		}
		if (data.update != null)
			doUpdateInfo(data.update);
	});
	return false;
}

function cleanBoiler() {
	var ptype	= $("#form_alchemy_boiler input:checked[type=radio][name=ptype]").val();
	var add_get	= '&do_cmd=clean_boiler&ptype=' + ptype + '&k=' + pkey;

	$.getJSON('?a=myguild&id=31&m=potion_1' + add_get, function(data){
		if (data.status == 'ok') {
			pkey	= data.data.GET_KEY;
			$('#alchemy_error_msg').html(data.data.message);
			$('#left_main').html(data.data.boiler_window);
			$('#right_main').html(data.data.action_window);
			blockForms();
			addItemInfo();
		}else {
			$('#alchemy_error_msg').html(data.data);
		}
		if (data.update != null)
			doUpdateInfo(data.update);
	});
}

function startMake() {
	var cleaner		= $('#slider_v_1').val();
	var assistent	= 0;
	if ($('#alchemy_use_assistent').is(':checked'))
		assistent	= 1;

	var add_get		= '&do_cmd=make_potion&k=' + pkey + '&cleaner_count=' + cleaner + '&use_assistent=' + assistent;

	$.getJSON('?a=myguild&id=31&m=potion_1' + add_get, function(data){
		if (data.status == 'ok') {
			pkey	= data.data.GET_KEY;
			$('#alchemy_error_msg').html(data.data.message);
			$('#left_main').html(data.data.boiler_window);
			$('#right_main').html(data.data.action_window);
			blockForms();
			addItemInfo();
		}else {
			$('#alchemy_error_msg').html(data.data);
		}
		if (data.update != null)
			doUpdateInfo(data.update);
	});
}

