function blockForms() {
	$("#form_alchemy_action").bind("submit", function(event){
		event.preventDefault();
	});

	$("#form_alchemy_boiler").bind("submit", function(event){
		event.preventDefault();
	});

	$("#form_alchemy_stir").bind("submit", function(event){
		event.preventDefault();
	});

	$("#form_alchemy_end").bind("submit", function(event){
		event.preventDefault();
	});
}

